mapper.select:
select 

from resource re left join role_resource rr on re.id = rr.resource_id
where rr.role_Id = #{roleId,jdbcType=INTEGER}

    select 

    from resource re left join role_resource rr on re.id = rr.resource_id 
    where re.enable = 1 and re.parent_id = #{parentId, jdbcType=INTEGER} 

	    and rr.role_id = #{roleId, jdbcType=INTEGER}

	order by re.sort asc, re.id asc

select rr.role_id as roleId,rr.resource_id as resourceId from  role_resource rr
left join resource  re  on rr.resource_id = re.id
where re.enable = 1 and rr.role_id = #{roleId,jdbcType=INTEGER}
